const formatarData = dataOriginal => {
    const dia = dataOriginal.substring(8, 10)
    const mes = dataOriginal.substring(5, 7)
    const ano = dataOriginal.substring(0, 4)
    return `${dia}/${mes}/${ano}`
}

export default formatarData
