import React,  {useEffect, useState} from 'react'

import { useParams } from 'react-router-dom'
import getDetalhesPost from '../../../utils/getDetalhesPost'

const PaginaPost = () => {

    const { id } = useParams()

    const [postCarregado, setPostCarregado] = useState([])

    useEffect(async () => {
        const _postCarregado = await getDetalhesPost(id)
        setPostCarregado( _postCarregado )
    },[])

    // const novoPost = {
    //     'imagem': imagem,
    //     'titulo': titulo,
    //     'autor': autor,
    //     'idTipoPostagem': parseInt(tipoPostagem),
    //     'descricao': descricao,
    //     'categoriaId': categoria,
    //     'dataPostagem': `${ano}-${mesFormatado}-${dia}`
    // }

    return (
        <>
            <h3>{postCarregado.titulo}</h3>
            <img src={postCarregado.imagem}/>
            <p>{postCarregado.autor}</p>
            <p>{postCarregado.dataPostagem}</p>
            <p>{postCarregado.descricao}</p>

        </>
    )
}

export default PaginaPost
