import React, {useState} from 'react'
import { Form, Button, Col } from 'react-bootstrap'
import salvarNovaCategoria from '../../../utils/salvarNovaCategoria'

const PaginaNovaCategoria = () => {

    const [novaCategoria, setNovaCategoria] = useState('')

    const incluirNovaCategoria = (e) => {

        //console.log('Form \'submitado\' ')

        e.preventDefault()

        if(
            !descricao
        ){
            alert('Por gentileza, preencha todos os campos.')
            return false
        }

        const novoPost = {
            'descricao': novaCategoria
        }

        salvarNovaCategoria(novoPost)
    }

    return (
        <>
            
            <Form onSubmit={ (e) => incluirNovaCategoria(e)}>
                <Form.Group as={Col}>
                    <h2>Nova Categoria</h2>
                </Form.Group>

                <Form.Group as={Col}>
                    <Form.Label htmlFor='np-campo-titulo'>Nome da categoria</Form.Label>
                    <Form.Control
                        id='np-campo-nova-categoria'
                        name='np-campo-nova-categoria'
                        value={novaCategoria}
                        onChange={e => setNovaCategoria(e.target.value)}
                    />
                </Form.Group>

                <Form.Group as={Col}>
                    <Button
                        type="submit" 
                        className="my-1"
                    >Salvar</Button>
                </Form.Group>
            </Form>
        </>
    )
}

export default PaginaNovaCategoria
