import React,  {useEffect, useState} from 'react'

import { useParams } from 'react-router-dom'
import getDetalhesPost from '../../../utils/getDetalhesPost'
import formatarData from '../../../utils/formatarData'

const PaginaPost = () => {

    const { id } = useParams()

    const [postCarregado, setPostCarregado] = useState([])

    useEffect(async () => {
        const _postCarregado = await getDetalhesPost(id)
        setPostCarregado( _postCarregado )
    },[])

    // const novoPost = {
    //     'imagem': imagem,
    //     'titulo': titulo,
    //     'autor': autor,
    //     'idTipoPostagem': parseInt(tipoPostagem),
    //     'descricao': descricao,
    //     'categoriaId': categoria,
    //     'dataPostagem': `${ano}-${mesFormatado}-${dia}`
    // }


    if(postCarregado.length){
        return (
            <>
            <h3>#{postCarregado.id} - {postCarregado.titulo}</h3>
            <h5>
                {postCarregado.idCategoria} - {formatarData(postCarregado.dataPostagem)} -
                Autor: {postCarregado.autor}
            </h5>
            <h5></h5>
            <img src={postCarregado.imagem}/>
            <p>{postCarregado.descricao}</p>
            </>
        )
    }
    return (<></>)
}

export default PaginaPost
