                <input
                    id='np-campo-titulo'
                    name='np-campo-titulo'
                />
            </div>

            <div className='np-campo'>
                <label htmlFor='np-campo-categoria'>Categoria</label>
                <select
                    id='np-campo-categoria'
                    name='np-campo-categoria'
                >
                    <option value={-1} disabled>Selecione uma categoria</option>
                </select>
            </div>

            <div className='np-campo'>
                <label htmlFor='np-campo-imagem'>Imagem</label>
                <input
                    id='np-campo-imagem'
                    name='np-campo-imagem'
                />
            </div>

            <div className='np-campo'>
                <label htmlFor='np-campo-descricao'>Descricao</label>
                <textarea
                    id='np-campo-descricao'
                    name='np-campo-descricao'
                />
            </div>
            {/* <form onSubmit={hendleSubmitForm}> */}
                {/* <div>
                    <label>Titulo: </label>
                    <input
                        value={formulario.titulo || ''}
                        name="titulo"
                        onChange={(e)=>{handleChangeForm(e)}}
                    />
                </div>

                <div>
                    <label>Categoria: </label>
                    <select
                        value={formulario.categoria || ''}
                        name="categoria"
                        onChange={(e)=>{handleChangeForm(e)}}
                    >
                        <option>Casa</option>
                        <option>Trabalho</option>
                    </select>
                </div>

                <button type="submit">enviar</button> */}
            </form>
        </>
    )
}

export default PaginaNovoPost
