import React, {useState} from 'react'
import { Form, Button, Col } from 'react-bootstrap'
import salvarNovaCategoria from '../../../utils/salvarNovaCategoria'

const PaginaNovaCategoria = ({categorias}) => {

    const [novaCategoria, setNovaCategoria] = useState('')

    const incluirNovoPost = (e) => {

        //console.log('Form \'submitado\' ')

        e.preventDefault()

        if(
            !imagem ||
            !titulo ||
            !descricao ||
            !categoria ||
            !autor ||
            !tipoPostagem
        ){
            alert('Por gentileza, preencha todos os campos.')
            return false
        }

        const hoje = new Date()

        const ano = hoje.getFullYear()
        const mes = hoje.getMonth()
        const dia = hoje.getDate()

        const mesFormatado = (mes + 1) < 10 ? "0" + String((mes + 1)) : (mes + 1)

        const novoPost = {
            'imagem': imagem,
            'titulo': titulo,
            'autor': autor,
            'idTipoPostagem': parseInt(tipoPostagem),
            'descricao': descricao,
            'categoriaId': categoria,
            'dataPostagem': `${ano}-${mesFormatado}-${dia}`
        }

        salvarNovaCategoria(novoPost)
    }

    return (
        <>
            
            <Form onSubmit={ (e) => incluirNovoPost(e)}>
                <Form.Group as={Col}>
                    <h2>Nova Categoria</h2>
                </Form.Group>

                <Form.Group as={Col}>
                    <Form.Label htmlFor='np-campo-titulo'>Nome da categoria</Form.Label>
                    <Form.Control
                        id='np-campo-nova-categoria'
                        name='np-campo-nova-categoria'
                        value={novaCategoria}
                        onChange={e => setNovaCategoria(e.target.value)}
                    />
                </Form.Group>

                <Form.Group as={Col}>
                    <Button
                        type="submit" 
                        className="my-1"
                    >Salvar</Button>
                </Form.Group>
            </Form>
        </>
    )
}

export default PaginaNovaCategoria
