import React, {useState} from 'react'
import { useParams } from 'react-router-dom'
import { Form, Button, Col, Row } from 'react-bootstrap'

import './PaginaNovoPost.css'

const PaginaNovoPost = () => {

    const [formulario, setFormulario] = useState({})

    const {criacao} = useParams()

    // const handleChangeForm = (event) => {
    //     const { name, value } = event.target
    //     setFormulario({
    //         ...formulario,
    //         [name]: value
    //     })
    // }

    // const hendleSubmitForm = (event) => {
    //     event.preventDefault()
    // }

    return (
        <>
            <h2>Novo Post</h2>
            <Form>
                <Form.Group as={Col}>
                    <Form.Label htmlFor='np-campo-titulo'>Titulo</Form.Label>
                    <Form.Control
                        id='np-campo-titulo'
                        name='np-campo-titulo'
                    />
                </Form.Group>

                <Form.Group as={Col}>
                    <Form.Label
                        htmlFor='np-campo-categoria'
                    >Categoria</Form.Label>
                    <Form.Control 
                        as="select"
                        defaultValue="Selecione uma categoria"
                        id='np-campo-categoria'
                        name='np-campo-categoria'
                    >
                        <option  disabled >Selecione uma categoria</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                    </Form.Control>
                </Form.Group>

                <div className='np-campo'>
                    <label htmlFor='np-campo-imagem'>Imagem</label>
                    <input
                        id='np-campo-imagem'
                        name='np-campo-imagem'
                    />
                </div>

                <Form.Group as={Col}>
                    <Form.Label htmlFor='np-campo-descricao'>Descricao</Form.Label>
                    <Form.Control 
                        as="textarea"
                        id='np-campo-descricao'
                        name='np-campo-descricao'
                    />
                </Form.Group>

                <Button
                    type="submit" 
                    className="my-1"
                >Salvar</Button>
                {/* <form onSubmit={hendleSubmitForm}> */}
                    {/* <div>
                        <label>Titulo: </label>
                        <input
                            value={formulario.titulo || ''}
                            name="titulo"
                            onChange={(e)=>{handleChangeForm(e)}}
                        />
                    </div>

                    <div>
                        <label>Categoria: </label>
                        <select
                            value={formulario.categoria || ''}
                            name="categoria"
                            onChange={(e)=>{handleChangeForm(e)}}
                        >
                            <option>Casa</option>
                            <option>Trabalho</option>
                        </select>
                    </div>

                    <button type="submit">enviar</button> */}
            </Form>
        </>
    )
}

export default PaginaNovoPost
