import React,{useContext, useState} from 'react'
import {Button, Spinner} from 'react-bootstrap'
import { Link } from 'react-router-dom'

import TemaContext from '../../../contexts/TemaContext'

import removerCategoriaServico from '../../../utils/removerCategoriaServico'

const PaginaCategorias = ({lista}) => {

    const tema = useContext(TemaContext)

    const [categoriaRemovida, setCategoriaRemovida] = useState(false)

    const removerCategoria = async () => {
        
        const resultado = await removerCategoriaServico(lista? lista.map(item=>{
            return (item.id)
        }): null)

        if(resultado.sucesso){
            setCategoriaRemovida(true)
            alert(resultado.mensagem)
            return false
        }
        setCategoriaRemovida(false)
    }

    return (
        <>
                <h3
                    style={{
                        'color':tema.fontColor
                    }}
                >Lista Categorias</h3>

                <Link to={`/nova-categoria`}>
                    <Button
                        variant="outline-success"
                    >Nova Categoria</Button>
                </Link>
                
                <ul>
                    { lista ? lista.map(item => {
                        return(
                            <li 
                                key={item.id}
                                style={{
                                    'color':tema.fontColor
                                }}
                            >
                                {item.descricao}
                                <Button
                                    onClick={() => removerCategoria()}
                                    style={{
                                        'marginLeft': '10px'
                                    }}
                                    variant="outline-danger"
                                >Remover</Button>
                            </li>
                            )                   
                        } 
                    ) : <Spinner animation="border" /> }
                </ul>
        </>
    )
}

export default PaginaCategorias
